import sysimport osfrom cx_Freeze import setup, Executablebase = Noneif sys.platform == "win32":    base = "Win32GUI"exe = Executable(script="vroomvroom.py", base=base)include_files = [os.path.join("resources", "img"), os.path.join("resources", "snd")]includes = []excludes = []packages = []setup(name="vroom vroom",         version="0.1",         description="a fun game",         options={"build_exe": {"includes": includes, "include_files": include_files,                                "packages": packages, "excludes": excludes}},         executables=[exe])